// n8n Function node code
// It reads the first input's .json.output (same as $input.first().json.output),
// extracts JSON (inside ```json ... ``` or the first {...} block), parses it,
// and returns the parsed object so next nodes can use fields like title/script.

const raw = (typeof $input === 'function' && $input().first && $input().first().json && $input().first().json.output)
  ? $input().first().json.output
  : (items && items[0] && items[0].json && items[0].json.output) || '';

// Helper: try to extract JSON block between triple backticks or fallback to first {...}
function extractJsonText(s) {
  if (!s || typeof s !== 'string') return null;

  // 1) Try to match ```json ... ``` (non-greedy)
  const fenced = s.match(/```(?:json)?\s*([\s\S]*?)```/i);
  if (fenced && fenced[1]) {
    return fenced[1].trim();
  }

  // 2) Fallback: find the first {...} JSON-looking block
  const braceMatch = s.match(/\{[\s\S]*\}/);
  if (braceMatch) {
    return braceMatch[0];
  }

  // Nothing found
  return null;
}

let parsed = null;
let extracted = extractJsonText(raw);

if (!extracted) {
  // If nothing to parse, return raw as output for debugging
  return [{ json: { error: 'No JSON found in input.output', raw } }];
}

try {
  // Some sources include Markdown-escaped quotes or extra backslashes.
  // Ensure we handle a common case where the extracted text itself is wrapped in triple quotes or fenced.
  // Trim and try to parse directly.
  const cleaned = extracted.trim();

  parsed = JSON.parse(cleaned);
} catch (err) {
  // If direct JSON.parse fails, try a safe 'unescape' pass (replace escaped newlines, etc.)
  try {
    // Replace common escape sequences that may be doubled (e.g. "\\n" => "\n")
    const unescaped = extracted
      .replace(/\\n/g, '\n')
      .replace(/\\t/g, '\t')
      .replace(/\\r/g, '\r')
      .replace(/\\"/g, '"');

    parsed = JSON.parse(unescaped);
  } catch (err2) {
    // Final fallback: return the extracted text and the error for debugging
    return [{ json: { error: 'Failed to parse JSON', message: err2.message, extracted } }];
  }
}

// Return parsed object so downstream nodes can access e.g. result.title, result.script
return [{ json: parsed }];

// Extract operation ID and preserve data
const veoResponse = $input.first().json;
const contentData = $node['Parse & Format Content'].first().json;

return {
  ...contentData,
  operationId: veoResponse.name || veoResponse.operationId,
  operationUrl: `https://videogeneration.googleapis.com/v1/${veoResponse.name || veoResponse.operationId}`
};

// Merge status check with original data
const statusData = $input.first().json;
const originalData = $node['Save Operation ID'].first().json;

if (!statusData.response || !statusData.response.videoUri) {
  throw new Error('Video generation failed - no video URL in response');
}

return {
  ...originalData,
  videoUrl: statusData.response.videoUri,
  videoDownloadUrl: statusData.response.downloadUri || statusData.response.videoUri
};

// Prepare data for Google Sheets
const uploadResult = $input.first().json;
const videoData = $node['Extract Video URL'].first().json;

const videoId = uploadResult.id;
const videoUrl = `https://youtube.com/shorts/${videoId}`;

return {
  'Channel Name': videoData.channelName,
  'Video Title': videoData.title,
  'Video Description': videoData.description.substring(0, 150) + '...',
  'Video URL': videoUrl,
  'Upload Timestamp': new Date().toLocaleString('en-IN', { timeZone: 'Asia/Kolkata' }),
  'Status': '‚úÖ Done',
  'Video ID': videoId
};

// Format error details
const error = $input.first().json;

return {
  errorMessage: error.error?.message || 'Unknown error',
  errorNode: error.error?.node?.name || 'Unknown node',
  errorTime: new Date().toLocaleString('en-IN', { timeZone: 'Asia/Kolkata' }),
  workflowName: $workflow.name,
  notificationEmail: $node['Config Variables'].json.notificationEmail
};

// Parse AI (Gemini or Veo3) JSON response and format it cleanly for downstream nodes

const response = $input.first().json;
const config = $input.first().json.output;

// Extract and parse JSON safely
let content;

try {
  // Access main AI text response
  const text = response.output || response.candidates?.[0]?.content?.parts?.[0]?.text || "";

  // Clean markdown or code fences (```json ... ```)
  const cleanText = text
    .replace(/```json\s*/gi, "")
    .replace(/```/g, "")
    .trim();

  // Parse JSON object
  content = JSON.parse(cleanText);
} catch (error) {
  throw new Error("Failed to parse AI response JSON: " + error.message);
}

// Build a smart YouTube-ready description
const description = `${content.script}\n\n${content.hashtags || ""}\n\n` +
  `üîî SUBSCRIBE for more amazing content!\n` +
  `üí™ Turn on notifications to never miss a video!\n\n` +
  `${content.hashtags || ""}`;

// Final structured return
return {
  channelName: config.channelName,
  title: content.title || "Untitled Video",
  script: content.script || "",
  hook: content.hook || "",
  description: description,
  tags: content.tags || [],
  hashtags: content.hashtags || "",
  veo3ProjectId: config.veo3ProjectId,
  veo3ApiKey: config.veo3ApiKey,
  timestamp: new Date().toISOString(),
};

